import { auth } from "../firebase/config";
import { signInAnonymously } from "firebase/auth";

const Login = () => {
  const handleLogin = async () => {
    try {
      await signInAnonymously(auth);
    } catch (error) {
      console.error("Giriş hatası:", error);
    }
  };
  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-indigo-500 via-purple-500 to-pink-500">
      <div className="p-10 bg-white/90 backdrop-blur-sm rounded-2xl shadow-2xl transform hover:scale-105 transition-all duration-300 max-w-md w-full mx-4">
        <h1 className="text-4xl font-bold mb-6 text-center bg-gradient-to-r from-indigo-500 to-pink-500 text-transparent bg-clip-text">
          Random Chat
        </h1>
        <p className="text-gray-600 mb-8 text-center">
          Yeni insanlarla tanışmaya hazır mısın?
        </p>
        <button
          onClick={handleLogin}
          className="w-full bg-gradient-to-r from-indigo-500 to-pink-500 text-white py-4 px-8 rounded-xl
          font-semibold hover:opacity-90 transform hover:-translate-y-0.5 transition-all duration-200
          focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 shadow-lg"
        >
          Sohbete Başla
        </button>
      </div>
    </div>
  );
};

export default Login;
