import { useEffect, useState } from "react";
import { db } from "../firebase/config";
import {
  doc,
  setDoc,
  collection,
  query,
  where,
  onSnapshot,
  deleteDoc,
} from "firebase/firestore";

const WaitingRoom = ({ user, onMatch }) => {
  const [searching, setSearching] = useState(true);

  useEffect(() => {
    if (!user) return;

    // Kullanıcıyı bekleme odasına ekle
    const addToWaitingRoom = async () => {
      const waitingRef = doc(db, "waitingRoom", user.uid);
      await setDoc(waitingRef, {
        userId: user.uid,
        timestamp: new Date().getTime(),
      });
    };

    // Eşleşme kontrolü
    const checkForMatch = () => {
      const waitingRoomRef = collection(db, "waitingRoom");
      const q = query(waitingRoomRef, where("userId", "!=", user.uid));

      return onSnapshot(q, async (snapshot) => {
        const waitingUsers = snapshot.docs.map((doc) => doc.data());

        if (waitingUsers.length > 0) {
          // Random bir kullanıcı seç
          const randomUser =
            waitingUsers[Math.floor(Math.random() * waitingUsers.length)];

          // Eşleşmeyi oluştur
          const chatId = [user.uid, randomUser.userId].sort().join("-");

          // Kullanıcıları bekleme odasından çıkar
          await deleteDoc(doc(db, "waitingRoom", user.uid));
          await deleteDoc(doc(db, "waitingRoom", randomUser.userId));

          onMatch(chatId, randomUser.userId);
          setSearching(false);
        }
      });
    };

    const unsubscribe = checkForMatch();
    addToWaitingRoom();

    return () => {
      unsubscribe();
      // Kullanıcı sayfadan ayrılırsa bekleme odasından çıkar
      deleteDoc(doc(db, "waitingRoom", user.uid));
    };
  }, [user]);

  const handleCancel = async () => {
    try {
      await deleteDoc(doc(db, "waitingRoom", user.uid));
      // Ana sayfaya yönlendirme veya başka bir işlem
    } catch (error) {
      console.error("İptal hatası:", error);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-indigo-500 via-purple-500 to-pink-500">
      <div
        className="p-10 bg-white/90 backdrop-blur-sm rounded-2xl shadow-2xl max-w-md w-full mx-4 
      transform hover:scale-[1.01] transition-all duration-300"
      >
        <h2
          className="text-3xl font-bold mb-8 text-center bg-gradient-to-r from-indigo-500 to-pink-500 
        text-transparent bg-clip-text animate-pulse"
        >
          Sohbet arkadaşı aranıyor...
        </h2>
        <div className="flex flex-col items-center space-y-8">
          <div className="relative">
            <div className="w-20 h-20 border-4 border-indigo-500 rounded-full animate-spin border-t-transparent"></div>
            <div className="w-20 h-20 border-4 border-pink-500 rounded-full animate-ping opacity-20 absolute top-0"></div>
          </div>
          <p className="text-gray-600 text-center text-lg">
            Size en uygun sohbet arkadaşını bulmaya çalışıyoruz
          </p>
          <button
            onClick={handleCancel}
            className="mt-6 px-6 py-2 text-red-500 border border-red-500 rounded-lg hover:bg-red-50 
            transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2"
          >
            Aramayı İptal Et
          </button>
        </div>
      </div>
    </div>
  );
};

export default WaitingRoom;
